mapper.insert:


        SELECT MAX(comment_id) + 1 from COMMENTS



	insert into comments
	(
	  comment_id
	, pa_comment_id
	, board_id
	, user_id
	, cmt_content	
	, create_date	
	) values
	(
	AAA.NEXTVAL
	,#{pa_comment_id}
	,#{board_id}
	,#{user_id}
	,#{cmt_content}
	, SYSDATE
	)

mapper.update:

UPDATE comments	
SET RECOMMENT_COUNT = RECOMMENT_COUNT + 1
WHERE comment_id = #{pa_comment_id}


UPDATE board
SET comment_count = comment_count + 1
WHERE board_id = #{board_id}


UPDATE comments
SET CMT_CONTENT = '삭제된 댓글입니다.', comment_state = 0
WHERE comment_id = #{comment_id}


UPDATE comments
SET cmt_content = #{cmt_content}
WHERE comment_id = #{comment_id}


UPDATE board
SET comment_count = comment_count - 1
WHERE board_id = #{board_id}


UPDATE comments	
SET RECOMMENT_COUNT = RECOMMENT_COUNT - 1
WHERE comment_id = #{pa_comment_id}

mapper.insert:

insert into COMMENT_LIKE
(
  comment_id
, user_id
, board_id
, cmt_like_ud
) values
(
#{comment_id}
,#{user_id}
,#{board_id}
,#{cmt_like_ud}
)

mapper.update:
UPDATE comments	
SET comment_like = comment_like + #{cmt_like_ud} 
WHERE comment_id = #{comment_id}

UPDATE comments	
SET comment_like = comment_like + 1
WHERE comment_id = #{comment_id}

UPDATE comments	
SET comment_like = comment_like + 2 
WHERE comment_id = #{comment_id}

UPDATE comments	
SET comment_like = comment_like - 1
WHERE comment_id = #{comment_id}

UPDATE comments	
SET comment_like = comment_like - 2
WHERE comment_id = #{comment_id}

mapper.delete:

delete 
from comment_like		
WHERE board_id = #{board_id} and user_id = #{user_id} and comment_id = #{comment_id}

mapper.update:
UPDATE comment_like	
SET cmt_like_ud = -1
WHERE board_id = #{board_id} and user_id = #{user_id} and comment_id = #{comment_id}

UPDATE comment_like	
SET cmt_like_ud = +1
WHERE board_id = #{board_id} and user_id = #{user_id} and comment_id = #{comment_id}
