insert into forum_section( section_id, forum_id, section_name, create_date )SELECT AAA.NEXTVAL, A.* FROM (
SELECT #{i.forum_id} AS forum_id ,#{i.section_name} AS section_name ,sysdate FROM DUAL
) A




        SELECT MAX(forum_id) + 1 from forums




